async function translateDeveloperDescriptions() {
  try {
    console.log('🔄 Connecting to database...');
    await AppDataSource.initialize();
    console.log('✅ Database connected\n');

    // Завантажуємо описи з developer.json для перекладу
    const developerJsonPath = path.join(__dirname, '../../developer.json');
    let developersJsonData: any[] = [];
    if (fs.existsSync(developerJsonPath)) {
      console.log('📖 Reading developer.json...');
      developersJsonData = JSON.parse(fs.readFileSync(developerJsonPath, 'utf-8'));
      console.log(`✅ Found ${developersJsonData.length} developers in JSON\n`);
    }

    // Створюємо мапу для швидкого пошуку
    const jsonDataMap = new Map<string, any>();
    developersJsonData.forEach((devData: any) => {
      const name = devData.name_en || devData.name || devData.title;
      if (name) {
        const normalizedName = name.toLowerCase().trim();
        jsonDataMap.set(normalizedName, devData);
      }
    });

    const developerRepository = AppDataSource.getRepository(Developer);
    const queryRunner = AppDataSource.createQueryRunner();
    await queryRunner.connect();

    try {
      // Отримуємо всіх девелоперів з описом
      const developersRaw = await queryRunner.query(`
        SELECT id, name, description
        FROM developers
        WHERE description IS NOT NULL
        ORDER BY name ASC
      `);

      console.log(`📖 Found ${developersRaw.length} developers with descriptions to process\n`);

      let updatedCount = 0;
      let skippedCount = 0;
      const notFound: string[] = [];
      const withoutRu: string[] = [];

      for (const row of developersRaw) {
        const developer = row;
        const currentDescription = developer.description;

        // Парсимо поточний опис
        let descObj: any = null;
        if (typeof currentDescription === 'string') {
          try {
            descObj = JSON.parse(currentDescription);
          } catch {
            descObj = { en: { description: currentDescription }, ru: {} };
          }
        } else if (typeof currentDescription === 'object') {
          descObj = currentDescription;
        }

        // Якщо немає структури, пропускаємо
        if (!descObj || !descObj.en || !descObj.en.description) {
          skippedCount++;
          continue;
        }

        // Перевіряємо, чи є переклад у словнику (нормалізуємо ім'я - прибираємо пробіли)
        const normalizedName = developer.name.trim();
        const translation = developerTranslations[normalizedName] || developerTranslations[developer.name];

        // Створюємо структуру
        let newDescription: any = {
          en: descObj.en || {},
          ru: descObj.ru || {},
        };

        // Якщо є ручний переклад у словнику, використовуємо його
        if (translation && translation.descriptionRu) {
          newDescription.ru = {
            description: translation.descriptionRu,
          };
          updatedCount++;
          console.log(`✅ [${updatedCount}/${developersRaw.length}] Updated (manual): ${developer.name}`);
        } else if (newDescription.en.description && !newDescription.ru.description) {
          // Якщо немає ручного перекладу, але є англійський опис
          // Можна додати автоматичний переклад або залишити порожнім
          // Поки що просто відмічаємо
          withoutRu.push(developer.name);
          skippedCount++;
        }

        // Оновлюємо через raw query для правильної обробки JSONB
        await queryRunner.query(
          `UPDATE developers SET description = $1::jsonb WHERE id = $2`,
          [JSON.stringify(newDescription), developer.id]
        );
      }

      console.log('\n📊 Translation Summary:');
      console.log(`   ✅ Updated (with Russian): ${updatedCount} developers`);
      console.log(`   ⚠️  Skipped (without RU translation): ${skippedCount} developers`);
      
      if (withoutRu.length > 0 && withoutRu.length <= 30) {
        console.log('\n⚠️  Developers without Russian translation (need manual translation):');
        withoutRu.slice(0, 30).forEach(name => console.log(`   • ${name}`));
        if (withoutRu.length > 30) {
          console.log(`   ... and ${withoutRu.length - 30} more`);
        }
        console.log(`\n   💡 Tip: Add translations to developerTranslations object in translate-developer-descriptions.ts`);
      }
      
      if (notFound.length > 0 && notFound.length <= 20) {
        console.log('\n⚠️  Developers not found:');
        notFound.forEach(name => console.log(`   • ${name}`));
      }

    } finally {
      await queryRunner.release();
    }

    await AppDataSource.destroy();
    console.log('\n✅ Translation completed!');
  } catch (error: any) {
    console.error('❌ Error:', error.message);
    if (error.stack) {
      console.error(error.stack);
    }
    if (AppDataSource.isInitialized) {
      await AppDataSource.destroy();
    }
    process.exit(1);
  }
}

translateDeveloperDescriptions();

